import axios from "axios";
import { useState } from "react";
import { useNavigate } from "react-router-dom";

const URI = 'http://localhost:8000/operations/'

const CreateOperation = () =>{
    const [concept, setConcept] = useState('')
    const [amount, setAmount] = useState('')
    const [date, setDate] = useState('')
    const [type, setType] = useState('')
    const [category, setCategory] = useState('')
    const navigate = useNavigate()
    

    // procedimiento para guardar
    const store = async (e) => {
        e.preventDefault()
        await axios.post(URI, {
            concept: concept, 
            amount: amount, 
            date: date, 
            type: type, 
            category: category
        })
        navigate('/')
        window.location.reload()
        
    }

    return (
        <div>
            
            <form onSubmit={store} className="card mx-5">
            <h3 className="card-header">Create Operation</h3>
                <div className="card-body">
                    <label className="form-label">concept</label>
                    <input
                        value={concept}
                        onChange={(e)=> setConcept(e.target.value)}
                        type="text"
                        className="form-control"
                        required
                    />
                </div>
                <div className="card-body">
                    <label className="form-label">amount</label>
                    <input
                        value={amount}
                        onChange={(e)=> setAmount(e.target.value)}
                        type="number"
                        placeholder="Cantidad en $"
                        className="form-control"
                        min={1}
                        required
                    />
                </div>
                <div className="mb-3 card-body">
                    <label className="form-label">date</label>
                    <input
                        value={date}
                        onChange={(e)=> setDate(e.target.value)}
                        type="date"
                        className="form-control"
                        required
                    />
                </div>
                <div className="btn-group btn-group-toggle">    
                    <label className="mx-5"> Ingreso
                        <input
                            type="radio"
                            value="ingreso"
                            onChange={(e)=> setType(e.target.value)}
                            name="tipo"
                            required
                            >
                        </input>
                    </label>
                    <label className="mx-5"> Egreso
                        <input
                            type="radio"
                            value="egreso"
                            onChange={(e)=> setType(e.target.value)}
                            name="tipo"
                            required
                            >
                        </input>
                    </label>
    
                </div>

                <button type="submit" className="btn btn-dark  btn-lg">Store</button>
                
            </form>
        </div>
    )
}

export default CreateOperation
