import axios from 'axios'
import { useState, useEffect } from 'react'
import {Link} from 'react-router-dom'

const URI = 'http://localhost:8000/operations/'

const ShowOperations = () => {
    const [operations, setOperation] = useState([])
    useEffect(()=>{
        getOperations()
    }, [])
    // Procedimiento para mostrar todas las operaciones
    const getOperations = async () => {
        const res = await axios.get(URI)
        setOperation(res.data)

    }
    // Procedimiento para eliminar una operacion
    const deleteOperation = async (id) => {
        await axios.delete(`${URI}${id}`)
        getOperations()
    }
    return(
        <div className='container'>
            <div className='row'>
                <div className='col'>
                    <Link to="/create" className='btn btn-primary mt-2 mb-2'><i class="fa-solid fa-plus"></i></Link>
                    <table className='table'>
                        <thead className='table-primary'>
                            <tr>
                                <th>Concept</th>
                                <th>Amount</th>
                                <th>Date</th>
                                <th>Type</th>
                                <th>Category</th>
                            </tr>
                        </thead>
                        <tbody>
                            { operations.map((operation) => (
                                <tr key={operation.id}>
                                    <td> {operation.conce} </td>
                                    <td> {operation.content} </td>
                                    <td>
                                        <Link to={`/edit/${operation.id}`} className="btn btn-info"><i class="fa-solid fa-pen-to-square"></i></Link>
                                        <button onClick={()=>deleteOperation(operation.id)} className='btn btn-danger'><i class="fa-solid fa-trash"></i></button>
                                    </td>
                                </tr>
                            )) }
                        </tbody>
                    </table>

                </div>

            </div>

        </div>
    )
}

export default ShowOperations
